import React, { Component } from 'react'; 
import {DropdownButton, Dropdown} from 'react-bootstrap' ; 
import List from './List';

class FilteredList extends Component {
	constructor(props) {
		super(props);
		this.state = {
			search: "",
			type: "All"
		};
	}
	
	onSearch = (event) => {
		this.setState({search: event.target.value.trim().toLowerCase()});
	}
	filterItem = (item) => {
		return item.name.toLowerCase().includes(this.state.search) &&
               (this.state.type === "All" || item.type === this.state.type);
	}
	/* TODO: Add an event handling method for when an item in dropdown is selected
	Per the DropdownButton documentation, this function should take in an eventKey and event
	*/
	render() {
		return (
			<div className="filter-list">
			<h1>Produce Search</h1>
			{/* TODO: Add more menu items with onSelect handlers*/}
			<DropdownButton id="typeDropdown" title={"Select Type"}>
				<MenuItem onClick={() => this.onSelectType("All")}>All</MenuItem>
                <MenuItem onClick={() => this.onSelectType("Fruit")}>Fruit</MenuItem>
                <MenuItem onClick={() => this.onSelectType("Vegetable")}>Vegetable</MenuItem>
			</DropdownButton>
			<input type="text" placeholder="Search" onChange={this.onSearch} />
			<List items={this.props.items.filter(this.filterItem)} />
			</div>
		);
	}
}

export default FilteredList;
